/* Polynomial interpolation version */
int32_t q31sin(int32_t x)
{
#if (FIXPT_SINCOS_INTRPL_POLY2)
	int32_t y321;
	int32_t y32;
	int32_t y3;
	int32_t x3;
	int32_t qx2;
#endif
	int32_t y21;
	int32_t y2;
	int32_t y1;
	int32_t y;
	int32_t x2;
	int32_t x1;
	int32_t qx1;
	int64_t acc;
	int32_t i;

	i = x >> (31 - LOG2_N);

	x1 = i << (31 - LOG2_N);
	y1 = qsintab[i++ & MASK_N] * ((x1 < 0) ? -1 : 1);

	x2 = x1 + DX;
	y2 = qsintab[i++ & MASK_N] * ((x2 < 0) ? -1 : 1);

	y21 = (y2 - y1);
	qx1 = Q31_DIV(x - x1, DX);

#if (FIXPT_SINCOS_INTRPL_POLY2)
	x3 = x2 + DX;
	y3 = qsintab[i & MASK_N] * ((x3 < 0) ? -1 : 1);

	y32 = (y3 - y2);
	y321 = (y32 - y21) / 2;

	qx2 = Q31_DIV(x - x2, DX);
#endif

	acc = y1 + Q31_MUL(y21, qx1);
#if (FIXPT_SINCOS_INTRPL_POLY2)
	acc += Q31_MUL(y321, Q31_MUL(qx1, qx2));
#endif
	y = Q31_SAT(acc);

	return y;
}
